mapper.select:
SELECT *
FROM pms_horsen.project

    is_deleted = 0
    AND name LIKE "%"#{name}"%"
    AND start_time = #{startTime}
    AND end_time = #{endTime}

mapper.update:
UPDATE pms_horsen.project

    name LIKE "%"#{name}"%"
    start_time = #{startTime}
    end_time = #{endTime}

WHERE id = #{id} AND is_deleted = 0
