select.where:
is_deleted = 0
AND name LIKE "%"#{name}"%"
AND age = #{age}
AND graduate_school LIKE "%"#{graduateSchool}"%"
AND degree = #{degree}
AND graduate_time = #{graduateTime}

mapper.update:
update pms_horsen.staff

    name = #{name}
    age = #{age}
    graduate_school = #{graduateSchool}
    degree = #{degree}
    graduate_time = #{graduateTime}

WHERE id = #{id} AND is_deleted = 0
