mapper.select:
select *
from pms_horsen.task

    is_deleted = 0
    and name like "%"#{name}"%"
    and start_time = #{startTime}
    and end_time = #{endTime}
    and proj_id = #{projId}
    and staff_id = #{staffId}

mapper.update:
update pms_horsen.task

    name like "%"#{name}"%"
    start_time = #{startTime}
    end_time = #{endTime}
    proj_id = #{projId}
    staff_id = #{staffId}

where id = #{id} and is_deleted = 0
